sp_widget.client_script:
function(glideUserSession) {
  /* widget controller */
  var c = this;
	c.data.hi = 5;
	//alert(c.data.currUser);
	c.trade = function(shift){
		var URL = "https://dev51362.service-now.com/sp?id=sc_cat_item&sys_id=2fe66f08db102300de3abd513996196e";
		URL += "&shiftid=" + shift.sys_id;
		window.open(URL);
	}
	c.take = function(shift){
		//window.open("google.com");
		//window.location.href = "google.com"
		glideUserSession.loadCurrentUser().then(function(currentUser) {
		c.data.userID = currentUser.userID;
		c.data.takeShift = true;
		c.data.shiftID = shift.sys_id;
		c.server.update();
		});

	}
	c.cover = function(shift){
		var URL = "https://dev51362.service-now.com/sp?id=sc_cat_item&sys_id=2fe66f08db102300de3abd513996196e";
		URL += "&coverid=" + shift.sys_id;
		window.open(URL);
	}
}

sp_widget.script:
(function() {

	if(input) {
		//Taking open shift
		if(input.takeShift){
			var gr = new GlideRecord('x_113761_ucd_shift_shifts');
			gr.addQuery('sys_id', input.shiftID);
			gr.query();
			if(gr.next()){
				gr.assigned_to = input.userID;
				gr.update();
			}
		}
	}

	data.shifts = getShifts();
	data.currUser = gs.getUserDisplayName();
	//need to filter to opening users group
	function getShifts() {
		var results = [];
		var now = new GlideDateTime();
		var gr = new GlideRecord('x_113761_ucd_shift_shifts');
		gr.orderBy("start");
		gr.query();

		while (gr.next()) {
			if (!$sp.canReadRecord(gr))
				continue;
			//Date Time var apparently not a GlideDateTime

			var gdt = new GlideDateTime(gr.start);

			//All weeks begin on sunday. if this week is current week
			if(gdt.getWeekOfYearUTC() == now.getWeekOfYearUTC()){
				var shift = {}
				//Get record information
				$sp.getRecordDisplayValues(shift, gr, 'assigned_to,start,end,sys_id');
				//Day of week as a num. 1 = Monday, 2 = tuesday
				shift.day = gdt.getDayOfWeekLocalTime();
				results.push(shift);
			}
		}				
		return results;
	}
})();
