var filterShifts = Class.create();
filterShifts.prototype = {
	initialize: function(){

	},

	filterByGroup : function(){
		var grMembers = new GlideRecord("sys_user_grmember");
		grMember.addQuery("group", current.variables.assignment_group);
		gs.addInfoMessage("RUNNING");
		var members = [];
		while(grMember.next()){
			members.push(grMember.getValue("user").toString() + ",");//Make sure right value
		} 
		return members;
	},
    type: 'filterShifts'
};
