var shiftid = current.variables.u_shifts;

var gr = new GlideRecord('x_113761_ucd_shift_shifts');
gr.addQuery('sys_id', shiftid);
gr.query();
if(gr.next()) {
	var app = new GlideRecord('sysapproval_approver');
	app.addQuery('document_id', current.sys_id);
	app.addQuery('state', 'approved');
	app.query();
	if(app.next()){
		gr.assigned_to = app.approver;
		gr.update();
	}
}

var shiftid = current.variables.u_shifts;

var gr = new GlideRecord('x_113761_ucd_shift_shifts');
gr.addQuery('sys_id', shiftid);
gr.query();
if(gr.next()) {
	var app = new GlideRecord('sysapproval_approver');
	app.addQuery('document_id', current.sys_id);
	app.addQuery('state', 'approved');
	app.query();
	if(app.next()){
		gr.assigned_to = app.approver;
		gr.update();
	}
}

var shift1 = current.variables.u_shifts;
var shift2 = current.variables.u_trade_shifts;
var hold;

var trade1 = new GlideRecord("x_113761_ucd_shift_shifts");
trade1.addQuery("sys_id", shift1);
trade1.query();

var trade2 = new GlideRecord("x_113761_ucd_shift_shifts");
trade2.addQuery("sys_id", shift2);
trade2.query();

if(trade1.next()){
	if(trade2.next()){
		//Swap
		hold = trade1.assigned_to + '';		
		trade1.assigned_to = trade2.assigned_to;
		trade2.assigned_to = hold;

		trade1.update();
		trade2.update();
	}
	
}

